  const wrapper = document.querySelector('.horizontal-wrapper');
  const scroller = document.querySelector('.horizontal-scroll');
  const panels = document.querySelectorAll('.panel');
  const indicator = document.querySelector('.scroll-indicator');
  const profile = document.getElementById('profile-icon');
  const dropdown = document.getElementById('profile-dropdown');

  // --- Horizontal scroll (only initialize if wrapper + scroller + panels exist) ---
  if (wrapper && scroller && panels.length) {
    const images = [
      'images/Scroll_page_1.png',
      'images/Scroll_page_2.png',
      'images/Scroll_page_3.png',
      'images/Scroll_page_4.png'
    ];

    function updateIndicator() {
      if (!indicator) return;
      let index = Math.round(scroller.scrollLeft / window.innerWidth);
      index = Math.max(0, Math.min(index, images.length - 1));
      indicator.src = images[index];
    }

    window.addEventListener('wheel', (e) => {
      const rect = wrapper.getBoundingClientRect();

      if (rect.top <= 0 && rect.bottom > window.innerHeight / 2) {
        const atFirstPanel = scroller.scrollLeft === 0;
        const atLastPanel = scroller.scrollLeft >= (panels.length - 1) * window.innerWidth;

        if ((atFirstPanel && e.deltaY < 0) || (atLastPanel && e.deltaY > 0)) {
          return;
        }

        e.preventDefault();
        scroller.scrollBy({
          left: e.deltaY > 0 ? window.innerWidth : -window.innerWidth,
          behavior: 'smooth'
        });
      }
    }, { passive: false });

    if (indicator) {
      indicator.addEventListener('click', () => {
        let index = Math.round(scroller.scrollLeft / window.innerWidth);
        let nextIndex = index + 1;
        if (nextIndex >= panels.length) nextIndex = 0;

        scroller.scrollTo({
          left: panels[nextIndex].offsetLeft,
          behavior: 'smooth'
        });
      });

      scroller.addEventListener('scroll', updateIndicator);
      updateIndicator();
    }
  }

if (profile && dropdown) {
  let hideTimeout;

  function showDropdown() {
    clearTimeout(hideTimeout);
    dropdown.classList.add('visible');
  }

  function hideDropdown() {
    hideTimeout = setTimeout(() => {
      dropdown.classList.remove('visible');
    }, 200); // small delay so you can move between icon & box
  }

  profile.addEventListener('mouseenter', showDropdown);
  dropdown.addEventListener('mouseenter', showDropdown);
  profile.addEventListener('mouseleave', hideDropdown);
  dropdown.addEventListener('mouseleave', hideDropdown);
}
