import React, { useState, useRef, useEffect } from 'react';
import Navigation from '../components/Navigation';
import { generateOutfitSuggestion, analyzeClosetItem } from '../services/geminiService';
import { ClothingItem } from '../types';

// Mock Data
const MOCK_ITEMS: ClothingItem[] = [
  { id: '1', type: 'top', name: 'White Linen Shirt', category: 'top', imageUrl: 'https://picsum.photos/300/300?random=1', wearCount: 12 },
  { id: '2', type: 'bottom', name: 'Navy Chinos', category: 'bottom', imageUrl: 'https://picsum.photos/300/300?random=2', wearCount: 5 },
  { id: '3', type: 'shoes', name: 'Brown Loafers', category: 'shoes', imageUrl: 'https://picsum.photos/300/300?random=3', wearCount: 20 },
  { id: '4', type: 'top', name: 'Graphic Tee', category: 'top', imageUrl: 'https://picsum.photos/300/300?random=4', wearCount: 1 },
  { id: '5', type: 'outerwear', name: 'Denim Jacket', category: 'outerwear', imageUrl: 'https://picsum.photos/300/300?random=5', wearCount: 8 },
];

const Dashboard: React.FC = () => {
  const [weather, setWeather] = useState({ temp: 18, condition: 'Sunny', city: 'San Francisco' });
  const [generatedLook, setGeneratedLook] = useState<{description: string, reasoning: string} | null>(null);
  const [isGenerating, setIsGenerating] = useState(false);
  const [activeSection, setActiveSection] = useState(0);
  const [newItemImage, setNewItemImage] = useState<string | null>(null);
  const [analysisResult, setAnalysisResult] = useState<any>(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const scrollContainerRef = useRef<HTMLDivElement>(null);

  // Gemini Outfit Generation
  const handleGenerateLook = async () => {
    setIsGenerating(true);
    try {
      const result = await generateOutfitSuggestion(
        `${weather.condition}, ${weather.temp}Â°C`,
        "Smart Casual",
        MOCK_ITEMS
      );
      setGeneratedLook(result);
    } catch (e) {
      console.error(e);
    } finally {
      setIsGenerating(false);
    }
  };

  // Closet Item Upload & Analysis
  const handleFileUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = async () => {
      const base64String = reader.result as string;
      const base64Data = base64String.split(',')[1]; // Remove data URL prefix
      setNewItemImage(base64String);

      setIsAnalyzing(true);
      const analysis = await analyzeClosetItem(base64Data);
      setAnalysisResult(analysis);
      setIsAnalyzing(false);
    };
    reader.readAsDataURL(file);
  };

  // Scroll Handling
  const scrollToSection = (index: number) => {
    if (scrollContainerRef.current) {
      const width = scrollContainerRef.current.offsetWidth;
      scrollContainerRef.current.scrollTo({
        left: index * width,
        behavior: 'smooth'
      });
      setActiveSection(index);
    }
  };

  const handleScroll = () => {
    if (scrollContainerRef.current) {
      const width = scrollContainerRef.current.offsetWidth;
      const index = Math.round(scrollContainerRef.current.scrollLeft / width);
      setActiveSection(index);
    }
  };

  useEffect(() => {
    const ref = scrollContainerRef.current;
    if (ref) {
      ref.addEventListener('scroll', handleScroll);
      return () => ref.removeEventListener('scroll', handleScroll);
    }
  }, []);

  return (
    <div className="bg-layer-bg min-h-screen font-sans flex flex-col">
      <Navigation />

      {/* Main Content Area */}
      <div className="flex-grow flex flex-col">

        {/* Banner / Hero for Dashboard */}
        <div className="w-full bg-cover bg-center text-white py-16 text-center shadow-lg relative"
             style={{ backgroundImage: 'linear-gradient(to bottom, rgba(107, 176, 216, 0.9), rgba(107, 176, 216, 0.7)), url("https://picsum.photos/1920/600?blur=4")' }}>
          <h1 className="text-4xl md:text-5xl font-bold mb-4">Style Smarter. Dress Better.</h1>
          <h2 className="text-xl md:text-2xl font-light">Your personal AI stylist is ready.</h2>
        </div>

        {/* Daily Look Section */}
        <div className="container mx-auto px-4 -mt-10 relative z-10 mb-12">
          <div className="bg-white rounded-xl shadow-xl p-8 max-w-4xl mx-auto border-t-8 border-layer-btn">
            <div className="flex justify-between items-center mb-6 border-b pb-4">
              <div className="text-layer-dark">
                <p className="text-2xl font-bold"><i className="fa-solid fa-sun mr-2 text-yellow-500"></i>{weather.city}</p>
                <p className="text-lg opacity-75">{weather.condition}, {weather.temp}Â°C</p>
              </div>
              <div className="text-right">
                <span className="bg-green-100 text-green-800 text-xs font-semibold px-2.5 py-0.5 rounded border border-green-400">Good Timing</span>
              </div>
            </div>

            <h2 className="text-3xl text-center text-layer-primary font-bold mb-8">Your Layer Look for Today</h2>

            <div className="flex justify-center gap-4 mb-8 flex-wrap md:flex-nowrap">
               {/* Outfit Images - Placeholders or Dynamic */}
               <img src="https://picsum.photos/200/250?random=10" alt="Top" className="w-32 md:w-48 h-40 md:h-64 object-cover rounded shadow-md hover:scale-105 transition" />
               <img src="https://picsum.photos/200/250?random=11" alt="Bottom" className="w-32 md:w-48 h-40 md:h-64 object-cover rounded shadow-md hover:scale-105 transition" />
               <img src="https://picsum.photos/200/250?random=12" alt="Shoes" className="w-32 md:w-48 h-40 md:h-64 object-cover rounded shadow-md hover:scale-105 transition" />
            </div>

            <div className="text-center">
              {generatedLook ? (
                <div className="mb-6 animate-fade-in">
                  <h3 className="text-2xl font-bold text-layer-dark">{generatedLook.description}</h3>
                  <p className="text-gray-600 italic mt-2">"{generatedLook.reasoning}"</p>
