import { GoogleGenAI, Type } from "@google/genai";
import { ClothingItem } from "../types";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

// 1. Context-Aware Outfit Generator (Enhanced)
export const generateOutfitSuggestion = async (
  weather: string,
  style: string,
  occasion: string, // New Parameter
  availableItems: ClothingItem[]
): Promise<{ description: string; reasoning: string }> => {

  const prompt = `
    You are a professional fashion stylist.
    Current Weather: ${weather}.
    User's Preferred Style: ${style}.
    Occasion: ${occasion}.

    Task: Suggest a complete outfit (Top, Bottom, Shoes, Accessory).

    Return a JSON object with:
    1. "description": A short, catchy title for the look.
    2. "reasoning": A 1-2 sentence explanation of why this works for the weather, style, and occasion.
  `;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      contents: prompt,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            description: { type: Type.STRING },
            reasoning: { type: Type.STRING },
          },
          required: ["description", "reasoning"],
        },
      },
    });

    const text = response.text;
    if (!text) throw new Error("No response from AI");

    return JSON.parse(text);
  } catch (error) {
    console.error("Error generating outfit:", error);
    return {
      description: "Casual Essentials",
      reasoning: "A safe and stylish choice when the AI is taking a coffee break.",
    };
  }
};

export const analyzeClosetItem = async (base64Image: string): Promise<{
  name: string;
  category: string;
  resaleEstimate: number;
  sustainabilityScore: number;
}> => {
   try {
    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      contents: {
        parts: [
          { inlineData: { mimeType: "image/jpeg", data: base64Image } },
          { text: "Analyze this clothing item. Identify what it is, categorize it (top, bottom, shoes, outerwear, accessory), estimate a resale value in USD for a used item in good condition, and give a sustainability score (1-10) based on perceived material quality." }
        ]
      },
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            name: { type: Type.STRING },
            category: { type: Type.STRING },
            resaleEstimate: { type: Type.NUMBER },
            sustainabilityScore: { type: Type.NUMBER },
          },
        }
      }
    });

    const text = response.text;
    if(!text) throw new Error("No analysis returned");
    return JSON.parse(text);

   } catch (error) {
     console.error("Error analyzing item:", error);
     return {
       name: "Unknown Item",
       category: "top",
       resaleEstimate: 5,
       sustainabilityScore: 5
     };
   }
}

// 2. AI Stylist Chat
export const chatWithStylist = async (message: string, history: {role: string, parts: {text: string}[]}[]): Promise<string> => {
  try {
    const chat = ai.chats.create({
      model: "gemini-2.5-flash",
      history: history,
      config: {
        systemInstruction: "You are a helpful, sassy, and knowledgeable fashion stylist assistant named LayerBot. Keep answers concise and fun."
      }
    });

    const result = await chat.sendMessage({ message });
    return result.text || "I'm having a bad hair day, try again later.";
  } catch (error) {
    console.error("Chat error:", error);
    return "I couldn't process that request right now.";
  }
};

// 3. Travel Packing List Generator
export const generatePackingList = async (destination: string, days: number, activity: string): Promise<{ categories: { name: string, items: string[] }[] }> => {
  const prompt = `Create a travel packing list for a ${days}-day trip to ${destination}. The main activity is: ${activity}.
  Group items into categories (Clothes, Toiletries, Tech, Misc).`;

  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      contents: prompt,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
             categories: {
               type: Type.ARRAY,
               items: {
                 type: Type.OBJECT,
                 properties: {
                   name: { type: Type.STRING },
                   items: { type: Type.ARRAY, items: { type: Type.STRING } }
                 }
               }
             }
          }
        }
      }
    });
    const text = response.text;
    if(!text) throw new Error("No response");
    return JSON.parse(text);
  } catch (error) {
    return { categories: [] };
  }
};

// 4. Trend Forecaster
export const getFashionTrends = async (): Promise<{ title: string; trends: { name: string, description: string }[] }> => {
  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      contents: "What are the top 3 current fashion trends for urban professionals right now? Return JSON.",
      config: {
        responseMimeType: "application/json",
        responseSchema: {
           type: Type.OBJECT,
           properties: {
             title: { type: Type.STRING },
             trends: {
               type: Type.ARRAY,
               items: {
                 type: Type.OBJECT,
                 properties: {
                   name: { type: Type.STRING },
                   description: { type: Type.STRING }
                 }
               }
             }
           }
        }
      }
    });
    const text = response.text;
    if(!text) throw new Error("No response");
    return JSON.parse(text);
  } catch (error) {
    return { title: "Trends Unavailable", trends: [] };
  }
};

