import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

interface NavigationProps {
  activeTab?: string;
  onTabChange?: (tab: string) => void;
}

const Navigation: React.FC<NavigationProps> = ({ activeTab, onTabChange }) => {
  const [isProfileOpen, setIsProfileOpen] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const navigate = useNavigate();

  const handleLogout = () => {
    navigate('/');
  };

  const handleNavClick = (tabName: string) => {
    if (onTabChange) {
      onTabChange(tabName);
    } else {
      navigate('/dashboard');
    }
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsProfileOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const navItems = [
    { id: 'wardrobe', label: 'Wardrobe' },
    { id: 'outfits', label: 'Outfits' },
    { id: 'planner', label: 'Planner' },
    { id: 'travel', label: 'Travel' },
  ];

  return (
    <header className="bg-layer-primary text-white p-4 flex justify-between items-center shadow-md relative z-50">
      <div className="flex items-center gap-2 cursor-pointer" onClick={() => handleNavClick('wardrobe')}>
         <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center text-layer-primary text-2xl font-bold">
           <i className="fa-solid fa-layer-group"></i>
         </div>
         <span className="text-xl font-bold tracking-wider hidden sm:block">LAYER</span>
      </div>

      <nav className="flex items-center gap-2 md:gap-4 overflow-x-auto no-scrollbar">
        {navItems.map(item => (
          <button 
            key={item.id}
            onClick={() => handleNavClick(item.id)} 
            className={`px-3 py-2 rounded transition font-medium whitespace-nowrap ${activeTab === item.id ? 'bg-white/20 font-bold border-b-2 border-white' : 'hover:bg-white/10 opacity-80 hover:opacity-100'}`}
          >
            {item.label}
          </button>
        ))}

        <div className="relative ml-2 md:ml-4" ref={dropdownRef}>
          <button 
            onClick={() => setIsProfileOpen(!isProfileOpen)}
            className="w-10 h-10 bg-white text-layer-primary rounded-full flex items-center justify-center hover:scale-110 transition-transform shadow-md focus:outline-none focus:ring-2 focus:ring-white/50"
          >
             <i className="fa-solid fa-user"></i>
          </button>

          {isProfileOpen && (
            <div className="absolute right-0 top-full mt-2 w-48 bg-layer-dark rounded-md shadow-lg overflow-hidden animate-fade-in text-left z-50 border border-white/10">
              <a href="#" className="block px-4 py-2 text-white hover:bg-white/10 transition"><i className="fa-solid fa-gear mr-2"></i> Settings</a>
              <a href="#" className="block px-4 py-2 text-white hover:bg-white/10 transition"><i className="fa-solid fa-user-circle mr-2"></i> Profile</a>
              <div className="border-t border-white/20 my-1"></div>
              <button onClick={handleLogout} className="block w-full text-left px-4 py-2 text-white hover:bg-white/10 transition">
                <i className="fa-solid fa-right-from-bracket mr-2"></i> Logout
              </button>
            </div>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Navigation;

