export const generateCapsule = async (items: ClothingItem[]): Promise<{ name: string; items: string[] }> => {
  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      contents: `Create a 5-item capsule wardrobe from this list: ${items.map(i => i.name).join(', ')}. Return JSON.`,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            name: { type: Type.STRING },
            items: { type: Type.ARRAY, items: { type: Type.STRING } }
          }
        }
      }
    });
    return JSON.parse(response.text || "{}");
  } catch (error) {
    return { name: "Mini Capsule", items: items.slice(0, 5).map(i => i.name) };
  }
};

export const analyzeWardrobeGaps = async (items: ClothingItem[]): Promise<{ missingItems: string[]; reasoning: string }> => {
  try {
    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      contents: `Analyze these clothes: ${items.map(i => i.name).join(', ')}. What key essentials are missing? Return JSON.`,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            missingItems: { type: Type.ARRAY, items: { type: Type.STRING } },
            reasoning: { type: Type.STRING }
          }
        }
      }
    });
    return JSON.parse(response.text || "{}");
  } catch (error) {
    return { missingItems: ["Basic White Tee"], reasoning: "Every closet needs basics." };
  }
};

