import { Search, Filter, Star, CheckCircle, XCircle, BrainCircuit } from 'lucide-react';

const mockTenants: Tenant[] = [
  {
    id: 't1',
    name: 'Sarah Jenkins',
    creditScore: 720,
    income: 8500,
    employmentStatus: 'Software Engineer at Google',
    bio: 'Quiet professional looking for a modern apartment. I work from home often so I need good light. Non-smoker, no pets.'
  },
  {
    id: 't2',
    name: 'Mike Ross',
    creditScore: 610,
    income: 4000,
    employmentStatus: 'Freelance Designer',
    bio: 'Creative individual looking for a studio. Income varies month to month but I have savings. Have a cat named Whiskers.'
  },
  {
    id: 't3',
    name: 'Emily & David',
    creditScore: 780,
    income: 12000,
    employmentStatus: 'Dual Income (Nurse & Teacher)',
    bio: 'Married couple looking for a long-term rental. We are very tidy and can provide excellent references from previous landlords.'
