// Mock property for context
const targetProperty: Property = {
  id: '2',
  name: 'Downtown Loft',
  address: '89 Main St',
  rent: 3200,
  status: 'Vacant',
  specs: { beds: 1, baths: 1.5, sqft: 1100 },
  description: '',
  imageUrl: ''
};

export const TenantFinder: React.FC = () => {
  const [tenants, setTenants] = useState<Tenant[]>(mockTenants);
  const [analyzingIds, setAnalyzingIds] = useState<string[]>([]);

  const handleAiMatch = async (tenantId: string) => {
    const tenant = tenants.find(t => t.id === tenantId);
    if (!tenant) return;

    setAnalyzingIds(prev => [...prev, tenantId]);
    const { score, reasoning } = await screenTenant(tenant, targetProperty);

    setTenants(prev => prev.map(t => 
      t.id === tenantId ? { ...t, matchScore: score, matchReasoning: reasoning } : t
    ));
    setAnalyzingIds(prev => prev.filter(id => id !== tenantId));
  };

  return (
    <div className="space-y-6">
      <div className="bg-indigo-900 rounded-xl p-8 text-white relative overflow-hidden">
        <div className="relative z-10">
          <h2 className="text-3xl font-bold mb-2">Find Your Perfect Tenant</h2>
          <p className="text-indigo-200 max-w-xl mb-6">Access our database of verified renters. Use AI to instantly screen applicants against your property criteria for risk assessment.</p>

          <div className="bg-white p-2 rounded-lg flex items-center max-w-md shadow-lg">
            <Search className="text-slate-400 ml-2" size={20} />
            <input 
              type="text" 
              placeholder="Search by name, job, or location..." 
              className="flex-1 p-2 outline-none text-slate-800 placeholder-slate-400"
            />
            <button className="bg-indigo-600 text-white px-4 py-2 rounded-md font-medium hover:bg-indigo-700">Search</button>
          </div>
        </div>
        <div className="absolute right-0 top-0 h-full w-1/3 bg-indigo-800 transform skew-x-12 translate-x-16 opacity-50 hidden md:block"></div>
      </div>

      <div className="flex justify-between items-center">
        <div className="flex items-center gap-2">
          <span className="text-sm font-bold text-slate-500 uppercase tracking-wide">Matching for:</span>
          <select className="bg-white border border-slate-200 rounded-md px-3 py-1 text-sm font-medium text-slate-700 outline-none">
            <option>Downtown Loft ($3,200/mo)</option>
            <option>Sunset Heights ($2,400/mo)</option>
          </select>
        </div>
        <button className="flex items-center gap-2 text-slate-600 hover:text-slate-900 text-sm font-medium">
          <Filter size={16} /> Filters
        </button>
      </div>

      <div className="grid gap-4">
        {tenants.map(tenant => (
          <div key={tenant.id} className="bg-white p-6 rounded-xl shadow-sm border border-slate-100 transition-all hover:shadow-md">
            <div className="flex flex-col md:flex-row gap-6">
              <div className="flex items-start gap-4 flex-1">
                <div className="w-16 h-16 rounded-full bg-slate-200 overflow-hidden shrink-0">
                  <img src={`https://picsum.photos/seed/${tenant.id}/200`} alt="Avatar" className="w-full h-full object-cover" />
                </div>
                <div>
                  <h3 className="text-lg font-bold text-slate-800 flex items-center gap-2">
                    {tenant.name}
                    {tenant.matchScore && tenant.matchScore > 80 && <Star size={16} className="text-yellow-400 fill-yellow-400" />}
                  </h3>
                  <p className="text-slate-500 text-sm">{tenant.employmentStatus}</p>

                  <div className="flex gap-4 mt-3">
                    <div className="bg-slate-50 px-3 py-1 rounded-md border border-slate-100">
                      <span className="text-xs text-slate-400 block">Credit Score</span>
                      <span className={`font-bold ${tenant.creditScore >= 700 ? 'text-green-600' : 'text-amber-600'}`}>
                        {tenant.creditScore}
                      </span>
                    </div>
                    <div className="bg-slate-50 px-3 py-1 rounded-md border border-slate-100">
                      <span className="text-xs text-slate-400 block">Monthly Income</span>
                      <span className="font-bold text-slate-700">${tenant.income.toLocaleString()}</span>
                    </div>
                  </div>

                  <p className="text-sm text-slate-600 mt-3 bg-slate-50 p-3 rounded-lg border border-slate-100 italic">
                    "{tenant.bio}"
                  </p>
                </div>
              </div>

              <div className="md:w-1/3 border-t md:border-t-0 md:border-l border-slate-100 pt-4 md:pt-0 md:pl-6 flex flex-col justify-center">
                {!tenant.matchScore ? (
                  <div className="text-center">
                    <p className="text-sm text-slate-500 mb-3">Not matched yet</p>
                    <button 
                      onClick={() => handleAiMatch(tenant.id)}
                      disabled={analyzingIds.includes(tenant.id)}
                      className="w-full py-2 bg-gradient-to-r from-indigo-600 to-violet-600 text-white rounded-lg font-medium hover:opacity-90 transition-opacity flex items-center justify-center gap-2"
                    >
                      {analyzingIds.includes(tenant.id) ? (
                        <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                      ) : (
                        <><BrainCircuit size={18} /> AI Screen Tenant</>
                      )}
                    </button>
                  </div>
                ) : (
                  <div className="bg-slate-50 rounded-lg p-4 border border-slate-200">
                     <div className="flex justify-between items-center mb-2">
                       <span className="text-sm font-bold text-slate-700">AI Match Score</span>
                       <span className={`text-xl font-bold ${
                         tenant.matchScore >= 80 ? 'text-green-600' : 
                         tenant.matchScore >= 50 ? 'text-amber-500' : 'text-red-500'
                       }`}>{tenant.matchScore}%</span>
                     </div>
                     <div className="w-full bg-slate-200 h-2 rounded-full mb-3">
                       <div 
                         className={`h-full rounded-full ${
                          tenant.matchScore >= 80 ? 'bg-green-500' : 
                          tenant.matchScore >= 50 ? 'bg-amber-500' : 'bg-red-500'
                         }`} 
                         style={{width: `${tenant.matchScore}%`}}
                       ></div>
                     </div>
                     <p className="text-xs text-slate-600 leading-relaxed">{tenant.matchReasoning}</p>

                     <div className="flex gap-2 mt-4">
                       <button className="flex-1 py-1.5 bg-green-600 text-white text-xs font-bold rounded hover:bg-green-700">Invite</button>
                       <button className="flex-1 py-1.5 border border-slate-300 text-slate-600 text-xs font-bold rounded hover:bg-slate-50">Dismiss</button>
                     </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { MaintenanceRequest } from '../types';
import { analyzeMaintenanceIssue } from '../services/geminiService';
import { Wrench, AlertCircle, Clock, CheckCircle2, Zap } from 'lucide-react';

const initialRequests: MaintenanceRequest[] = [
  {
    id: 'm1',
    propertyId: '1',
    tenantName: 'Alice Cooper',
    issue: 'The water heater is making a loud banging noise and the water isn\'t getting very hot.',
    status: 'Pending',
    priority: 'Medium',
    dateSubmitted: '2023-10-24',
  },
  {
    id: 'm2',
    propertyId: '1',
    tenantName: 'Bob Builder',
    issue: 'There is a small leak under the kitchen sink.',
    status: 'In Progress',
    priority: 'Low',
    dateSubmitted: '2023-10-22',
  }
];

export const MaintenanceHub: React.FC = () => {
  const [requests, setRequests] = useState<MaintenanceRequest[]>(initialRequests);
  const [processingId, setProcessingId] = useState<string | null>(null);

  const handleAiTriage = async (req: MaintenanceRequest) => {
    setProcessingId(req.id);
    const analysis = await analyzeMaintenanceIssue(req.issue);

    setRequests(prev => prev.map(r => 
      r.id === req.id ? { 
        ...r, 
        priority: analysis.priority as any, 
        aiCategory: analysis.category,
