import React, { useState, useRef, useEffect } from 'react';
import { askLandlordAdvisor } from '../services/geminiService';
import { Send, Bot, User } from 'lucide-react';

interface Message {
  id: string;
  role: 'user' | 'model';
  text: string;
}

export const AiAdvisor: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([
    { id: '0', role: 'model', text: 'Hello! I am PropPilot AI. I can help with lease questions, local property laws, or general management advice. How can I assist you today?' }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;

    const userMsg: Message = { id: Date.now().toString(), role: 'user', text: input };
    setMessages(prev => [...prev, userMsg]);
    setInput('');
    setIsLoading(true);

    // Format history for the service
    const history = messages.map(m => ({
      role: m.role,
      parts: [{ text: m.text }]
    }));

    const responseText = await askLandlordAdvisor(userMsg.text, history);

    setMessages(prev => [...prev, { id: (Date.now() + 1).toString(), role: 'model', text: responseText }]);
    setIsLoading(false);
  };

  return (
    <div className="h-[calc(100vh-8rem)] flex flex-col bg-white rounded-xl shadow-sm border border-slate-100 overflow-hidden">
      <div className="p-4 border-b border-slate-100 bg-slate-50 flex justify-between items-center">
        <div>
           <h2 className="text-lg font-bold text-slate-800 flex items-center gap-2">
            <Bot className="text-blue-600" /> PropPilot Advisor
           </h2>
           <p className="text-xs text-slate-500">AI-powered legal & management assistant</p>
        </div>
        <div className="text-xs text-slate-400 bg-white px-2 py-1 rounded border border-slate-200">
          Powered by Gemini
        </div>
      </div>

      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map(msg => (
          <div key={msg.id} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
            <div className={`flex items-start gap-3 max-w-[80%] ${msg.role === 'user' ? 'flex-row-reverse' : ''}`}>
              <div className={`w-8 h-8 rounded-full flex items-center justify-center shrink-0 ${
                msg.role === 'user' ? 'bg-slate-200 text-slate-600' : 'bg-blue-600 text-white'
              }`}>
                {msg.role === 'user' ? <User size={16} /> : <Bot size={16} />}
              </div>
              <div className={`p-3 rounded-2xl text-sm leading-relaxed ${
                msg.role === 'user' 
                  ? 'bg-slate-800 text-white rounded-tr-none' 
                  : 'bg-slate-100 text-slate-800 rounded-tl-none'
              }`}>
                {msg.text}
              </div>
            </div>
          </div>
        ))}
        {isLoading && (
          <div className="flex justify-start">
             <div className="flex items-start gap-3">
               <div className="w-8 h-8 rounded-full bg-blue-600 text-white flex items-center justify-center">
                 <Bot size={16} />
               </div>
               <div className="bg-slate-100 p-4 rounded-2xl rounded-tl-none">
                 <div className="flex gap-1">
                   <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{animationDelay: '0ms'}}></div>
                   <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{animationDelay: '150ms'}}></div>
                   <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{animationDelay: '300ms'}}></div>
                 </div>
               </div>
             </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <div className="p-4 border-t border-slate-100 bg-white">
        <div className="flex gap-2 relative">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Ask about evictions, lease clauses, or repair costs..."
            className="flex-1 border border-slate-200 rounded-lg pl-4 pr-4 py-3 outline-none focus:border-blue-500 transition-colors"
          />
          <button 
            onClick={handleSend}
            disabled={isLoading || !input.trim()}
            className="bg-blue-600 text-white px-4 rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-colors flex items-center gap-2"
          >
            <Send size={18} />
          </button>
        </div>
        <p className="text-center text-xs text-slate-400 mt-2">AI can make mistakes. Please consult a real lawyer for legal advice.</p>
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { ViewState } from './types';
import { Layout } from './components/Layout';
import { Dashboard } from './components/Dashboard';
import { PropertyManager } from './components/PropertyManager';
import { TenantFinder } from './components/TenantFinder';
import { MaintenanceHub } from './components/MaintenanceHub';
import { Community } from './components/Community';
import { AiAdvisor } from './components/AiAdvisor';
import { hasApiKey } from './services/geminiService';

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<ViewState>(ViewState.DASHBOARD);
  const [apiKeyMissing, setApiKeyMissing] = useState(!hasApiKey());

  const renderContent = () => {
    if (apiKeyMissing) {
      // Small banner for missing API key if needed, but the UI is mostly functional without it (mock responses)
      // We will render content normally but features might show errors as per service implementation.
      // However, let's just show a small warning at the top of layout if we wanted.
    }

    switch (currentView) {
      case ViewState.DASHBOARD:
        return <Dashboard />;
      case ViewState.PROPERTIES:
        return <PropertyManager />;
      case ViewState.TENANTS:
        return <TenantFinder />;
      case ViewState.MAINTENANCE:
        return <MaintenanceHub />;
      case ViewState.COMMUNITY:
        return <Community />;
      case ViewState.AI_ASSISTANT:
        return <AiAdvisor />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <Layout currentView={currentView} onNavigate={setCurrentView}>
      {apiKeyMissing && (
        <div className="bg-amber-50 border border-amber-200 text-amber-800 px-4 py-2 rounded-lg mb-4 text-sm flex justify-between items-center">
          <span>
            <strong>Warning:</strong> No Gemini API Key found. AI features will use mock data or fail. 
            Set <code>REACT_APP_GEMINI_API_KEY</code> or <code>process.env.API_KEY</code>.
          </span>
          <button 
            onClick={() => setApiKeyMissing(false)} 
            className="text-amber-900 font-bold hover:underline"
          >
            Dismiss
          </button>
        </div>
      )}
      {renderContent()}
    </Layout>
  );
};

export default App;

