          <span className="px-4 py-2 bg-amber-50 text-amber-600 border border-amber-100 rounded-full text-sm font-bold flex items-center gap-2 shadow-sm">
            <Clock size={16} /> 5 Pending
          </span>
        </div>
      </div>

      <div className="grid gap-6">
        {requests.map(req => (
          <div key={req.id} className="bg-white rounded-2xl shadow-sm border border-slate-100 overflow-hidden hover:shadow-md transition-shadow">
            <div className="p-6">
              <div className="flex justify-between items-start mb-6">
                <div className="flex items-start gap-4">
                  <div className={`mt-1 p-3 rounded-2xl shadow-sm ${
                    req.status === 'Resolved' ? 'bg-green-100 text-green-600' :
                    req.priority === 'High' ? 'bg-red-100 text-red-600' : 'bg-blue-100 text-blue-600'
                  }`}>
                    <Wrench size={24} />
                  </div>
                  <div>
                    <h3 className="text-xl font-bold text-slate-800 mb-1">{req.issue}</h3>
                    <p className="text-sm text-slate-500 font-medium">Reported by <span className="text-slate-700">{req.tenantName}</span> • {req.dateSubmitted}</p>
                  </div>
                </div>
                <div className="flex flex-col items-end gap-2">
                   <span className={`px-3 py-1 rounded-full text-xs font-bold uppercase tracking-wide border ${
                     req.status === 'Pending' ? 'bg-amber-50 text-amber-700 border-amber-100' :
                     req.status === 'In Progress' ? 'bg-blue-50 text-blue-700 border-blue-100' : 'bg-green-50 text-green-700 border-green-100'
                   }`}>
                     {req.status}
                   </span>
                   {req.aiCategory && (
                     <span className="text-xs bg-slate-100 text-slate-600 px-2 py-1 rounded-lg font-bold border border-slate-200">
                       {req.aiCategory}
                     </span>
                   )}
                </div>
              </div>

              {!req.aiAnalysis ? (
                <div className="mt-4 bg-slate-50 p-5 rounded-xl flex items-center justify-between border border-slate-200 border-dashed">
                   <p className="text-sm text-slate-500 font-medium">Use AI to categorize this issue and suggest vendors.</p>
                   <button 
                     onClick={() => handleAiTriage(req)}
                     disabled={processingId === req.id}
                     className="px-5 py-2.5 bg-gradient-to-r from-orange-500 to-pink-500 text-white text-sm font-bold rounded-xl hover:shadow-lg hover:shadow-pink-500/20 transition-all flex items-center gap-2"
