        <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500"></div>
        <div className="absolute inset-0 bg-gradient-to-b from-transparent to-slate-900/90 z-0"></div>
