import React, { useState } from 'react';
import { ViewState, UserRole } from './types';
import { Layout } from './components/Layout';
import { Dashboard } from './components/Dashboard';
import { PropertyManager } from './components/PropertyManager';
import { TenantFinder } from './components/TenantFinder';
import { MaintenanceHub } from './components/MaintenanceHub';
import { Community } from './components/Community';
import { AiAdvisor } from './components/AiAdvisor';
import { LandingPage } from './components/LandingPage';
import { TenantDashboard } from './components/TenantDashboard';
import { MaintenanceDashboard } from './components/MaintenanceDashboard';
import { hasApiKey } from './services/geminiService';

const App: React.FC = () => {
  const [currentView, setCurrentView] = useState<ViewState>(ViewState.LANDING);
  const [userRole, setUserRole] = useState<UserRole>('LANDLORD');
  const [apiKeyMissing, setApiKeyMissing] = useState(!hasApiKey());

  const handleLogin = (role: UserRole) => {
    setUserRole(role);
    // Set default view based on role
    if (role === 'LANDLORD') setCurrentView(ViewState.DASHBOARD);
    else if (role === 'TENANT') setCurrentView(ViewState.MY_UNIT);
    else if (role === 'MAINTENANCE') setCurrentView(ViewState.JOBS);
  };

  const handleLogout = () => {
    setCurrentView(ViewState.LANDING);
    setUserRole('LANDLORD');
  };

  if (currentView === ViewState.LANDING) {
    return <LandingPage onLogin={handleLogin} />;
  }

  const renderContent = () => {
    // Shared Views
    if (currentView === ViewState.COMMUNITY) return <Community />;
    if (currentView === ViewState.AI_ASSISTANT) return <AiAdvisor />;

    // Role Specific Routing
    if (userRole === 'LANDLORD') {
      switch (currentView) {
        case ViewState.DASHBOARD: return <Dashboard />;
        case ViewState.PROPERTIES: return <PropertyManager />;
        case ViewState.TENANTS: return <TenantFinder />;
        case ViewState.MAINTENANCE: return <MaintenanceHub />;
        default: return <Dashboard />;
      }
    } else if (userRole === 'TENANT') {
      switch (currentView) {
        case ViewState.MY_UNIT: return <TenantDashboard />;
        case ViewState.MAINTENANCE: return <TenantDashboard />; // Simplified for now, or create separate MaintenanceRequestForm
        default: return <TenantDashboard />;
      }
    } else if (userRole === 'MAINTENANCE') {
      switch (currentView) {
        case ViewState.JOBS: return <MaintenanceDashboard />;
        default: return <MaintenanceDashboard />;
      }
    }
  };

  return (
    <Layout 
      currentView={currentView} 
      userRole={userRole}
      onNavigate={setCurrentView}
      onLogout={handleLogout}
    >
      {apiKeyMissing && (
        <div className="bg-amber-50 border border-amber-200 text-amber-800 px-4 py-2 rounded-lg mb-4 text-sm flex justify-between items-center">
          <span>
            <strong>Warning:</strong> No Gemini API Key found. AI features will use mock data or fail. 
            Set <code>REACT_APP_GEMINI_API_KEY</code> or <code>process.env.API_KEY</code>.
          </span>
          <button 
            onClick={() => setApiKeyMissing(false)} 
            className="text-amber-900 font-bold hover:underline"
          >
            Dismiss
          </button>
        </div>
      )}
      {renderContent()}
    </Layout>
  );
};

export default App;

export type UserRole = 'LANDLORD' | 'TENANT' | 'MAINTENANCE';

export interface Property {
  id: string;
  name: string;
  address: string;
  rent: number;
  status: 'Occupied' | 'Vacant' | 'Maintenance';
  description: string;
  specs: {
    beds: number;
    baths: number;
    sqft: number;
  };
  imageUrl: string;
  isVerified?: boolean; // New field
}

export interface Tenant {
  id: string;
  name: string;
  creditScore: number;
  income: number;
  employmentStatus: string;
  bio: string;
  matchScore?: number; // AI calculated
  matchReasoning?: string; // AI generated
}

export interface MaintenanceRequest {
  id: string;
  propertyId: string;
  tenantName: string;
  issue: string;
  status: 'Pending' | 'In Progress' | 'Resolved';
  priority: 'Low' | 'Medium' | 'High';
  dateSubmitted: string;
  aiAnalysis?: string; // AI generated suggestions
  aiCategory?: string;
}

export interface ForumPost {
  id: string;
  author: string;
  title: string;
  content: string;
  likes: number;
  comments: number;
  tags: string[];
}

export enum ViewState {
  LANDING = 'LANDING',

  // Landlord Views
  DASHBOARD = 'DASHBOARD',
  PROPERTIES = 'PROPERTIES',
  TENANTS = 'TENANTS',

  // Tenant Views
  MY_UNIT = 'MY_UNIT',

  // Maintenance Views
  JOBS = 'JOBS',

  // Shared Views
  MAINTENANCE = 'MAINTENANCE',
  COMMUNITY = 'COMMUNITY',
  AI_ASSISTANT = 'AI_ASSISTANT'
}

import { GoogleGenAI, Type } from "@google/genai";
import { Property, Tenant } from "../types";

const apiKey = process.env.API_KEY || '';
const ai = new GoogleGenAI({ apiKey });

// Helper to check if API key exists
export const hasApiKey = (): boolean => !!apiKey;

export const generateListingDescription = async (property: Property): Promise<string> => {
  if (!apiKey) return "API Key missing. Please configure your environment.";

  try {
    const prompt = `
      Write a compelling, SEO-friendly rental listing description for the following property. 
      Target high-quality tenants.

      Details:
      - Name: ${property.name}
      - Type: ${property.specs.beds} Bed, ${property.specs.baths} Bath
      - Size: ${property.specs.sqft} sqft
      - Price: $${property.rent}/month
      - Address: ${property.address}

      Keep it professional yet inviting. Max 150 words.
    `;

    const response = await ai.models.generateContent({
      model: 'gemini-2.5-flash',
      contents: prompt,
    });
    return response.text || "Could not generate description.";
  } catch (error) {
    console.error("Gemini Error:", error);
    return "Error generating description. Please try again.";
  }
};

export const analyzeMaintenanceIssue = async (issueText: string): Promise<{ priority: string, category: string, suggestion: string }> => {
  if (!apiKey) return { priority: 'Medium', category: 'General', suggestion: 'Manual review required.' };

  try {
    const prompt = `
      Analyze this maintenance request from a tenant: "${issueText}".

      Return a JSON object with:
      1. priority: "High", "Medium", or "Low"
      2. category: e.g., "Plumbing", "Electrical", "Appliance", "HVAC", "Structural"
      3. suggestion: One sentence advice for the landlord on who to call or what to check first.
    `;

    const response = await ai.models.generateContent({
      model: 'gemini-2.5-flash',
      contents: prompt,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            priority: { type: Type.STRING },
            category: { type: Type.STRING },
            suggestion: { type: Type.STRING }
          }
        }
      }
    });

    const text = response.text;
    if (!text) throw new Error("No text response");
    return JSON.parse(text);
  } catch (error) {
    console.error("Gemini Error:", error);
    return { priority: 'Medium', category: 'Unknown', suggestion: 'AI analysis failed.' };
  }
};

export const screenTenant = async (tenant: Tenant, property: Property): Promise<{ score: number, reasoning: string }> => {
  if (!apiKey) return { score: 0, reasoning: 'API Key missing' };

  try {
    const prompt = `
      Evaluate if this tenant is a good fit for the property based on typical landlord criteria (Income 3x rent rule, credit score > 650 is good).

      Property Rent: $${property.rent}
      Tenant Income: $${tenant.income}
      Tenant Credit Score: ${tenant.creditScore}
      Tenant Employment: ${tenant.employmentStatus}
      Tenant Bio: "${tenant.bio}"

      Return JSON:
      {
        "score": (number 0-100),
        "reasoning": (string, brief explanation of the score)
      }
    `;

    const response = await ai.models.generateContent({
      model: 'gemini-2.5-flash',
      contents: prompt,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            score: { type: Type.INTEGER },
            reasoning: { type: Type.STRING }
          }
        }
      }
    });

    const text = response.text;
    if (!text) throw new Error("No text response");
    return JSON.parse(text);
  } catch (error) {
    console.error("Gemini Error:", error);
    return { score: 50, reasoning: 'AI scoring unavailable.' };
  }
};

export const verifyDocument = async (docDescription: string): Promise<{ verified: boolean, confidence: number, reasoning: string }> => {
  if (!apiKey) return { verified: false, confidence: 0, reasoning: "API Key missing" };

  try {
    const prompt = `
      You are a strict verification agent. Analyze the following text description of a document uploaded by a landlord or vendor.
      Determine if it sounds like a legitimate proof of ownership (Deed, Title) or License.

      Document Description: "${docDescription}"

      Return JSON:
      {
        "verified": (boolean),
        "confidence": (number 0-100),
        "reasoning": (string)
      }
    `;

    const response = await ai.models.generateContent({
      model: 'gemini-2.5-flash',
      contents: prompt,
      config: {
        responseMimeType: "application/json",
        responseSchema: {
          type: Type.OBJECT,
          properties: {
            verified: { type: Type.BOOLEAN },
            confidence: { type: Type.INTEGER },
            reasoning: { type: Type.STRING }
          }
        }
      }
    });

    const text = response.text;
    if (!text) throw new Error("No text response");
    return JSON.parse(text);
  } catch (error) {
    console.error("Gemini Error:", error);
    return { verified: false, confidence: 0, reasoning: "AI verification failed." };
  }
};

export const askLandlordAdvisor = async (question: string, history: {role: string, parts: {text: string}[]}[] = []): Promise<string> => {
  if (!apiKey) return "Please provide an API key to use the AI advisor.";

  try {
    const chat = ai.chats.create({
      model: 'gemini-2.5-flash',
      history: [
        {
          role: 'user',
          parts: [{ text: "You are an expert real estate and landlord advisor. Give concise, legal-aware (but not legal advice), and practical answers." }]
        },
        {
          role: 'model',
          parts: [{ text: "Understood. I am PropPilot AI, ready to assist with landlord inquiries." }]
        },
        ...history
      ]
    });

    const result = await chat.sendMessage({ message: question });
    return result.text || "I'm thinking...";
  } catch (error) {
    console.error("Gemini Error:", error);
    return "Sorry, I encountered an error answering that.";
  }
};

